import React, { useState } from 'react';
import Sidebar from './components/Sidebar';
import ChatInterface from './components/ChatInterface';
import Settings from './components/Settings';
import FinancePage from './components/FinancePage';
import './App.css';

type ViewType = 'chat' | 'settings' | 'finance';

interface Transaction {
  id: string;
  type: 'income' | 'expense';
  amount: number;
  category: string;
  description: string;
  date: Date;
}

const App: React.FC = () => {
  const [currentView, setCurrentView] = useState<ViewType>('chat');
  const [transactions, setTransactions] = useState<Transaction[]>([]);

  const handleTransactionAdd = (transaction: Transaction) => {
    setTransactions(prev => [...prev, transaction]);
  };

  return (
    <div className="app">
      <Sidebar onViewChange={setCurrentView} currentView={currentView} />
      <div className="main-content">
        {currentView === 'chat' && <ChatInterface onTransactionAdd={handleTransactionAdd} />}
        {currentView === 'settings' && <Settings />}
        {currentView === 'finance' && <FinancePage transactions={transactions} />}
      </div>
    </div>
  );
};

export default App;
