  const handleSendMessage = async () => {
    if (!inputText.trim()) return;

    const transaction = parseTransaction(inputText);
    console.log('Parsed transaction:', transaction); // Debug log
    
    const userMessage: Message = {
      id: Date.now().toString(),
      text: inputText,
      sender: 'user',
      timestamp: new Date(),
      transaction: transaction || undefined
    };

    onMessageAdd?.(userMessage);
    
    // Save transaction to Supabase if one was parsed
    if (transaction) {
      console.log('Saving transaction to Supabase:', transaction); // Debug log
      try {
        const { data, error } = await transactionsApi.insertTransaction(transaction);
        if (error) {
          console.error('Error saving transaction to Supabase:', error);
          // Still add to local state even if Supabase fails
          onTransactionAdd?.(transaction);
        } else {
          console.log('Transaction saved successfully to Supabase:', data); // Debug log
          // Transaction saved successfully to Supabase
          onTransactionAdd?.(transaction);
        }
      } catch (error) {
        console.error('Error saving transaction to Supabase:', error);
        // Still add to local state even if Supabase fails
        onTransactionAdd?.(transaction);
      }
    } else {
      console.log('No transaction parsed from message:', inputText); // Debug log
    }
    
    setInputText('');
    setIsTyping(true);

    // Simulate ANITA typing
    setTimeout(() => {
      const anitaResponse: Message = {
        id: (Date.now() + 1).toString(),
        text: getAnitaResponse(inputText, transaction || undefined),
        sender: 'anita',
        timestamp: new Date()
      };
      
      onMessageAdd?.(anitaResponse);
      setIsTyping(false);
    }, 1500);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <div className="chat-interface">
      <div className="chat-header">
