import React, { useState, useRef, useEffect } from 'react';
import { Send, Bot, User, Trash2, Brain } from 'lucide-react';
import './ChatInterface.css';

interface Message {
  id: string;
  text: string;
  sender: 'user' | 'anita';
  timestamp: Date;
  transaction?: {
    type: 'income' | 'expense';
    amount: number;
    category: string;
    description: string;
  };
}

interface Transaction {
  id: string;
  type: 'income' | 'expense';
  amount: number;
  category: string;
  description: string;
  date: Date;
}

interface ChatInterfaceProps {
  onTransactionAdd?: (transaction: Transaction) => void;
  onMessageAdd?: (message: Message) => void;
  onClearConversation?: () => void;
  messages: Message[];
}

const ChatInterface: React.FC<ChatInterfaceProps> = ({ onTransactionAdd, onMessageAdd, onClearConversation, messages }) => {
  const [inputText, setInputText] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const parseTransaction = (message: string): Transaction | null => {
    // Enhanced regex patterns for better detection
    const incomePatterns = [
      /(?:income|earned|made|received|got|deposited|added)\s+\$?(\d+(?:\.\d{2})?)\s*(?:from|for|at|as)?\s*(.+)/i,
      /(?:salary|wage|bonus|payment|refund|rebate)\s+(?:of\s+)?\$?(\d+(?:\.\d{2})?)/i,
      /\$(\d+(?:\.\d{2})?)\s+(?:income|earned|received|made|deposited|added)/i,
      /(?:plus|positive|gain)\s+\$?(\d+(?:\.\d{2})?)/i,
      /(?:bank|account)\s+(?:deposit|credit)\s+(?:of\s+)?\$?(\d+(?:\.\d{2})?)/i,
      /(?:i\s+have|my\s+balance\s+is|i\s+got)\s+\$?(\d+(?:\.\d{2})?)/i,
      /(?:freelance|freelancing|gig|side\s+hustle)\s+(?:for\s+)?\$?(\d+(?:\.\d{2})?)/i
    ];
    
    const expensePatterns = [
      /(?:spent|expense|bought|paid|cost|withdrew|deducted)\s+\$?(\d+(?:\.\d{2})?)\s*(?:on|for|at|for)?\s*(.+)/i,
      /(?:purchase|bought|paid|billed)\s+(?:for\s+)?(.+?)\s+(?:for\s+)?\$?(\d+(?:\.\d{2})?)/i,
      /\$(\d+(?:\.\d{2})?)\s+(?:spent|expense|cost|paid|withdrew|deducted)/i,
      /(?:minus|negative|loss)\s+\$?(\d+(?:\.\d{2})?)/i,
      /(?:bank|account)\s+(?:withdrawal|debit)\s+(?:of\s+)?\$?(\d+(?:\.\d{2})?)/i,
      /(?:bill|invoice|charge)\s+(?:of\s+)?\$?(\d+(?:\.\d{2})?)/i,
      /(?:i\s+owe|i\s+need\s+to\s+pay|debt\s+of)\s+\$?(\d+(?:\.\d{2})?)/i,
      /(?:subscription|monthly|annual)\s+(?:fee|cost|charge)\s+(?:of\s+)?\$?(\d+(?:\.\d{2})?)/i
    ];
    
    // Check for income
    for (const pattern of incomePatterns) {
      const match = message.match(pattern);
      if (match) {
        const amount = parseFloat(match[1] || match[2]);
        const description = match[2] || match[1] || 'Income';
        
        return {
          id: Date.now().toString(),
          type: 'income',
          amount: amount,
          category: 'Income',
          description: description.trim(),
          date: new Date()
        };
      }
    }
    
    // Check for expenses
    for (const pattern of expensePatterns) {
      const match = message.match(pattern);
      if (match) {
        const amount = parseFloat(match[1] || match[2]);
        const description = match[2] || match[1] || 'Expense';
        
        // Try to categorize the expense
        let category = 'Other';
        const desc = description.toLowerCase();
        if (desc.includes('food') || desc.includes('restaurant') || desc.includes('grocery') || desc.includes('dining') || desc.includes('lunch') || desc.includes('dinner')) {
          category = 'Food';
        } else if (desc.includes('gas') || desc.includes('transport') || desc.includes('uber') || desc.includes('taxi') || desc.includes('fuel') || desc.includes('parking')) {
          category = 'Transport';
        } else if (desc.includes('rent') || desc.includes('housing') || desc.includes('mortgage') || desc.includes('utilities') || desc.includes('electric') || desc.includes('water')) {
          category = 'Housing';
        } else if (desc.includes('entertainment') || desc.includes('movie') || desc.includes('game') || desc.includes('netflix') || desc.includes('spotify') || desc.includes('subscription')) {
          category = 'Entertainment';
        } else if (desc.includes('health') || desc.includes('medical') || desc.includes('doctor') || desc.includes('pharmacy') || desc.includes('insurance')) {
          category = 'Healthcare';
        } else if (desc.includes('shopping') || desc.includes('clothes') || desc.includes('clothing') || desc.includes('amazon') || desc.includes('store')) {
          category = 'Shopping';
        } else if (desc.includes('education') || desc.includes('school') || desc.includes('course') || desc.includes('book') || desc.includes('tuition')) {
          category = 'Education';
        }
        
        return {
          id: Date.now().toString(),
          type: 'expense',
          amount: amount,
          category: category,
          description: description.trim(),
          date: new Date()
        };
      }
    }
    
    return null;
  };

  const getAnitaResponse = (userMessage: string, transaction?: Transaction): string => {
    // Get recent conversation context
    const recentMessages = messages.slice(-6); // Last 6 messages for context
    const hasRecentTransactions = recentMessages.some(m => m.transaction);
    const recentTransactionCount = recentMessages.filter(m => m.transaction).length;
    
    if (transaction) {
