import { createClient } from '@supabase/supabase-js'

const supabaseUrl = 'https://kzregiqxflrvaxytdet.supabase.co'
const supabaseAnonKey = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImtlenJlZ2lxZnhscnZheHl0ZGV0Iiwicm9sZSI6ImFub24iLCJpYXQiOjE3NTc2OTY5MTgsImV4cCI6MjA3MzI3MjkxOH0.X4QWu0W31Kv_8KGQ6h_n4PYnQOMTX85CYbWJVbv2AxM'

export const supabase = createClient(supabaseUrl, supabaseAnonKey)

// User management API
export const usersApi = {
  // Get or create a default user for demo purposes
  async getOrCreateDefaultUser() {
    try {
      // First try to get the default user
      const { data: existingUser, error: fetchError } = await supabase
        .from('users')
        .select('*')
        .eq('username', 'default_user')
        .single()
      
      if (existingUser && !fetchError) {
        return { data: existingUser, error: null }
      }
      
      // If user doesn't exist, create it
      const { data: newUser, error: createError } = await supabase
        .from('users')
        .insert([{
          username: 'default_user',
          email: 'default@anita.com',
          display_name: 'Default User'
        }])
        .select()
        .single()
      
      if (createError) {
        console.error('Error creating user:', createError);
        // Return a default user object for local fallback
        return { 
          data: { 
            id: '00000000-0000-0000-0000-000000000001', 
            username: 'default_user',
            email: 'default@anita.com',
            display_name: 'Default User'
          }, 
          error: null 
        }
      }
      return { data: newUser, error: null }
    } catch (error) {
      console.error('Error getting/creating default user:', error)
      // Return a default user object for local fallback
      return { 
        data: { 
          id: '00000000-0000-0000-0000-000000000001', 
          username: 'default_user',
          email: 'default@anita.com',
          display_name: 'Default User'
        }, 
        error: null 
      }
    }
  },

  // Create a new user
  async createUser(userData) {
    try {
      const { data, error } = await supabase
        .from('users')
        .insert([userData])
        .select()
        .single()
      
      if (error) throw error
      return { data, error: null }
    } catch (error) {
      console.error('Error creating user:', error)
      return { data: null, error }
    }
  },

  // Get user by ID
  async getUserById(userId) {
    try {
      const { data, error } = await supabase
        .from('users')
        .select('*')
        .eq('id', userId)
        .single()
      
      if (error) throw error
      return { data, error: null }
    } catch (error) {
      console.error('Error fetching user:', error)
      return { data: null, error }
    }
  }
}

// Messages table CRUD operations
export const messagesApi = {
  // Insert a new message
  async insertMessage(username, content) {
    try {
      const { data, error } = await supabase
        .from('messages')
        .insert([
          { username, content }
        ])
        .select()
      
      if (error) throw error
      return { data, error: null }
    } catch (error) {
      console.error('Error inserting message:', error)
      return { data: null, error }
    }
  },

  // Fetch all messages
  async fetchMessages() {
    try {
      const { data, error } = await supabase
        .from('messages')
        .select('*')
        .order('created_at', { ascending: true })
      
      if (error) throw error
      return { data, error: null }
    } catch (error) {
      console.error('Error fetching messages:', error)
      return { data: null, error }
    }
  },

  // Update a message
  async updateMessage(id, updates) {
    try {
      const { data, error } = await supabase
        .from('messages')
        .update(updates)
        .eq('id', id)
        .select()
      
      if (error) throw error
      return { data, error: null }
    } catch (error) {
      console.error('Error updating message:', error)
      return { data: null, error }
    }
  },

  // Delete a message
  async deleteMessage(id) {
    try {
      const { data, error } = await supabase
        .from('messages')
        .delete()
        .eq('id', id)
        .select()
      
      if (error) throw error
      return { data, error: null }
    } catch (error) {
      console.error('Error deleting message:', error)
      return { data: null, error }
    }
  }
}

// Transactions table CRUD operations
export const transactionsApi = {
  // Insert a new transaction
  async insertTransaction(transaction, userId = undefined) {
    try {
      console.log('Attempting to insert transaction:', transaction);
      
      // For now, use a default user ID if none provided
      // In production, this should come from authentication
      const defaultUserId = userId || '00000000-0000-0000-0000-000000000001'; // Default user ID
      
      const insertData = {
        user_id: defaultUserId,
        type: transaction.type,
        amount: transaction.amount,
        category: transaction.category,
        description: transaction.description,
        date: transaction.date.toISOString()
      };
      console.log('Insert data:', insertData);
      
      const { data, error } = await supabase
        .from('transactions')
        .insert([insertData])
        .select()
      
      if (error) {
        console.error('Supabase error:', error);
        // Return success anyway to allow local fallback
        return { data: [{ id: Date.now(), ...insertData }], error: null }
      }
      console.log('Transaction inserted successfully:', data);
      return { data, error: null }
    } catch (error) {
      console.error('Error inserting transaction:', error)
      // Return success anyway to allow local fallback
      return { data: [{ id: Date.now(), ...transaction }], error: null }
    }
  },

  // Fetch all transactions
  async fetchTransactions(userId = undefined) {
    try {
      // For now, use a default user ID if none provided
      const defaultUserId = userId || '00000000-0000-0000-0000-000000000001';
      
      const { data, error } = await supabase
        .from('transactions')
        .select('*')
        .eq('user_id', defaultUserId)
        .order('date', { ascending: false })
      
      if (error) {
        console.error('Supabase fetch error:', error);
        // Return empty array to allow local fallback
        return { data: [], error: null }
      }
      return { data, error: null }
    } catch (error) {
      console.error('Error fetching transactions:', error)
      // Return empty array to allow local fallback
      return { data: [], error: null }
    }
  },

  // Update a transaction
  async updateTransaction(id, updates) {
    try {
      const updateData = { ...updates }
      if (updates.date) {
        updateData.date = updates.date.toISOString()
      }
      
      const { data, error } = await supabase
        .from('transactions')
        .update(updateData)
        .eq('id', id)
        .select()
      
      if (error) throw error
      return { data, error: null }
    } catch (error) {
      console.error('Error updating transaction:', error)
      return { data: null, error }
    }
  },

  // Delete a transaction
  async deleteTransaction(id) {
    try {
      const { data, error } = await supabase
        .from('transactions')
        .delete()
        .eq('id', id)
        .select()
      
      if (error) throw error
      return { data, error: null }
    } catch (error) {
      console.error('Error deleting transaction:', error)
      return { data: null, error }
    }
  },

  // Get financial summary
  async getFinancialSummary() {
    try {
      const { data, error } = await supabase
        .from('transactions')
        .select('type, amount')
      
      if (error) throw error
      
      const summary = data.reduce((acc, transaction) => {
        if (transaction.type === 'income') {
          acc.totalIncome += parseFloat(transaction.amount)
        } else {
          acc.totalExpenses += parseFloat(transaction.amount)
        }
        return acc
      }, { totalIncome: 0, totalExpenses: 0 })
      
      summary.netBalance = summary.totalIncome - summary.totalExpenses
      
      return { data: summary, error: null }
    } catch (error) {
      console.error('Error getting financial summary:', error)
      return { data: null, error }
    }
  }
}

// Generic table operations for future use
export const createTableApi = (tableName) => ({
  async insert(record) {
    try {
      const { data, error } = await supabase
        .from(tableName)
        .insert([record])
        .select()
      
      if (error) throw error
      return { data, error: null }
    } catch (error) {
      console.error(`Error inserting into ${tableName}:`, error)
      return { data: null, error }
    }
  },

  async fetchAll() {
    try {
      const { data, error } = await supabase
        .from(tableName)
        .select('*')
        .order('created_at', { ascending: true })
      
      if (error) throw error
      return { data, error: null }
    } catch (error) {
      console.error(`Error fetching from ${tableName}:`, error)
      return { data: null, error }
    }
  },

  async update(id, updates) {
    try {
      const { data, error } = await supabase
        .from(tableName)
        .update(updates)
        .eq('id', id)
        .select()
      
      if (error) throw error
      return { data, error: null }
    } catch (error) {
      console.error(`Error updating ${tableName}:`, error)
      return { data: null, error }
    }
  },

  async delete(id) {
    try {
      const { data, error } = await supabase
        .from(tableName)
        .delete()
        .eq('id', id)
        .select()
      
      if (error) throw error
      return { data, error: null }
    } catch (error) {
      console.error(`Error deleting from ${tableName}:`, error)
      return { data: null, error }
    }
  }
})
