import React, { useState, useRef, useEffect } from 'react';
import { Send, Bot, User } from 'lucide-react';
import { saveTransaction, saveMessage } from '../supabaseClient';
import './ChatInterface.css';

interface Message {
  id: string;
  text: string;
  sender: 'user' | 'anita';
  timestamp: Date;
  transaction?: {
    type: 'income' | 'expense';
    amount: number;
    category: string;
    description: string;
  };
}

interface Transaction {
  id: string;
  type: 'income' | 'expense';
  amount: number;
  category: string;
  description: string;
  date: Date;
}

interface ChatInterfaceProps {
  onTransactionAdd?: (transaction: Transaction) => void;
  onMessageAdd?: (message: Message) => Promise<void>;
  messages: Message[];
  currentUserId?: string | null;
}

const ChatInterface: React.FC<ChatInterfaceProps> = ({ onTransactionAdd, onMessageAdd, messages, currentUserId }) => {
  const [inputText, setInputText] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const parseTransaction = (message: string): Transaction | null => {
    console.log('Parsing message:', message);
    
    // Clean the message
    const cleanMessage = message.trim().toLowerCase();
    
    // Extract number from message (handles $, commas, decimals)
    const numberMatch = cleanMessage.match(/(?:^|\s)(-?\$?\d+(?:,\d{3})*(?:\.\d{2})?)(?:\s|$)/);
    if (!numberMatch) {
      console.log('No number found in message');
      return null;
    }
    
    // Parse the amount
    let amountStr = numberMatch[1].replace(/[$,]/g, ''); // Remove $ and commas
    const amount = parseFloat(amountStr);
    
    if (isNaN(amount)) {
      console.log('Invalid number:', amountStr);
      return null;
    }
    
    console.log('Extracted amount:', amount);
    
    // Determine if it's income or expense based on keywords
    const incomeKeywords = ['income', 'earned', 'made', 'received', 'got', 'deposited', 'added', 'salary', 'wage', 'bonus', 'payment', 'refund', 'rebate', 'plus', 'positive', 'gain'];
    const expenseKeywords = ['spent', 'expense', 'bought', 'paid', 'cost', 'withdrew', 'deducted', 'bill', 'charge', 'purchase', 'minus', 'negative', 'loss', 'debt', 'owe'];
    
    const hasIncomeKeyword = incomeKeywords.some(keyword => cleanMessage.includes(keyword));
    const hasExpenseKeyword = expenseKeywords.some(keyword => cleanMessage.includes(keyword));
    
    // If message is just a number, treat as income by default
    if (!hasIncomeKeyword && !hasExpenseKeyword && /^-?\d+(?:\.\d{2})?$/.test(cleanMessage)) {
      console.log('Standalone number, treating as income');
      return {
        id: Date.now().toString(),
        type: 'income',
        amount: Math.abs(amount), // Ensure positive for income
        category: 'Income',
        description: 'Income',
        date: new Date()
      };
    }
    
    // If message starts with negative number, treat as expense
    if (amount < 0 || cleanMessage.startsWith('-')) {
      console.log('Negative number, treating as expense');
      return {
        id: Date.now().toString(),
        type: 'expense',
        amount: Math.abs(amount), // Make positive for expense
        category: 'Other',
        description: 'Expense',
        date: new Date()
      };
    }
    
    // Determine type based on keywords
    let type: 'income' | 'expense';
    let description: string;
    
    if (hasIncomeKeyword && !hasExpenseKeyword) {
      type = 'income';
      description = 'Income';
    } else if (hasExpenseKeyword && !hasIncomeKeyword) {
      type = 'expense';
      description = 'Expense';
    } else if (hasIncomeKeyword && hasExpenseKeyword) {
      // If both keywords present, use the one that appears first
      const incomeIndex = Math.min(...incomeKeywords.map(keyword => cleanMessage.indexOf(keyword)).filter(i => i !== -1));
      const expenseIndex = Math.min(...expenseKeywords.map(keyword => cleanMessage.indexOf(keyword)).filter(i => i !== -1));
      
      if (incomeIndex < expenseIndex) {
        type = 'income';
        description = 'Income';
      } else {
        type = 'expense';
        description = 'Expense';
      }
    } else {
      // Default to income if no clear keywords
      type = 'income';
      description = 'Income';
    }
    
    // Try to extract description from the message
    const words = cleanMessage.split(/\s+/);
    const descriptionWords = words.filter(word => 
      !incomeKeywords.includes(word) && 
      !expenseKeywords.includes(word) && 
      !word.match(/^-?\$?\d+(?:,\d{3})*(?:\.\d{2})?$/) &&
      word.length > 1
    );
    
    if (descriptionWords.length > 0) {
      description = descriptionWords.join(' ').trim();
    }
    
    // Categorize expenses
    let category = type === 'income' ? 'Income' : 'Other';
    if (type === 'expense') {
      const desc = description.toLowerCase();
      if (desc.includes('food') || desc.includes('restaurant') || desc.includes('grocery') || desc.includes('dining') || desc.includes('lunch') || desc.includes('dinner')) {
        category = 'Food';
      } else if (desc.includes('gas') || desc.includes('transport') || desc.includes('uber') || desc.includes('taxi') || desc.includes('fuel') || desc.includes('parking')) {
        category = 'Transport';
      } else if (desc.includes('rent') || desc.includes('housing') || desc.includes('mortgage') || desc.includes('utilities') || desc.includes('electric') || desc.includes('water')) {
        category = 'Housing';
      } else if (desc.includes('entertainment') || desc.includes('movie') || desc.includes('game') || desc.includes('netflix') || desc.includes('spotify') || desc.includes('subscription')) {
        category = 'Entertainment';
      } else if (desc.includes('health') || desc.includes('medical') || desc.includes('doctor') || desc.includes('pharmacy') || desc.includes('insurance')) {
        category = 'Healthcare';
      } else if (desc.includes('shopping') || desc.includes('clothes') || desc.includes('clothing') || desc.includes('amazon') || desc.includes('store')) {
        category = 'Shopping';
      } else if (desc.includes('education') || desc.includes('school') || desc.includes('course') || desc.includes('book') || desc.includes('tuition')) {
        category = 'Education';
      }
    }
    
    console.log('Final result:', { type, amount, category, description });
    
    return {
      id: Date.now().toString(),
      type,
      amount,
      category,
      description,
      date: new Date()
    };
  };

  const getAnitaResponse = (userMessage: string, transaction?: Transaction): string => {
    if (transaction) {
      if (transaction.type === 'income') {
        const responses = [
          `Nice! ðŸ’° I've added $${transaction.amount} income for "${transaction.description}" to your records. Keep that money flowing! ðŸ’…`,
          `Awesome! ðŸ’š $${transaction.amount} income from "${transaction.description}" has been recorded. You're doing great! âœ¨`,
          `Perfect! ðŸ’¸ I've logged your $${transaction.amount} income for "${transaction.description}". Keep up the good work! ðŸ’ª`,
          `Excellent! ðŸ’° Your $${transaction.amount} income from "${transaction.description}" is now tracked. Money moves! ðŸš€`
        ];
        return responses[Math.floor(Math.random() * responses.length)];
      } else {
        const responses = [
          `Got it! ðŸ’¸ I've recorded $${transaction.amount} expense for "${transaction.description}". Let's keep track of those spending habits! ðŸ˜‰`,
          `Noted! ðŸ’³ $${transaction.amount} spent on "${transaction.description}" has been logged. Stay mindful of your budget! ðŸ’­`,
          `Recorded! ðŸ“ I've added your $${transaction.amount} expense for "${transaction.description}". Keep tracking! ðŸ“Š`,
          `Done! âœ… $${transaction.amount} expense for "${transaction.description}" is now in your records. Stay on top of it! ðŸŽ¯`
        ];
        return responses[Math.floor(Math.random() * responses.length)];
      }
    }
    
    const responses = [
      "Tell me about your money moves! ðŸ’° You can say things like 'I spent $50 on groceries' or 'I earned $200 from freelancing'",
      "What's the financial tea today? â˜• Share your income or expenses with me!",
      "Spill the financial beans! ðŸ’… I'm here to track your money flow",
      "Let's talk money! ðŸ’¸ Share what you've earned or spent today",
      "What's happening with your wallet? ðŸ’³ Tell me about any transactions!",
      "Money talk time! ðŸ’° Share your income or expenses with me",
      "Let's get financial! ðŸ“Š Tell me about your money moves today",
      "What's the financial update? ðŸ’… I'm ready to track your transactions!"
    ];
    
    return responses[Math.floor(Math.random() * responses.length)];
  };

  const handleSendMessage = async () => {
    if (!inputText.trim()) return;

    const transaction = parseTransaction(inputText);
    console.log('Parsed transaction:', transaction); // Debug log
    
    const userMessage: Message = {
      id: Date.now().toString(),
      text: inputText,
      sender: 'user',
      timestamp: new Date(),
      transaction: transaction || undefined
    };

    await onMessageAdd?.(userMessage);
    
    // Save transaction to Supabase if one was parsed
    if (transaction && currentUserId) {
      console.log('Saving transaction to Supabase:', transaction);
      try {
        await saveTransaction({
          user_id: currentUserId,
          type: transaction.type,
          amount: transaction.amount,
          category: transaction.category,
          description: transaction.description
        }, currentUserId);
        
        console.log('Transaction saved successfully to Supabase');
        // Transaction saved successfully to Supabase - no need to add to local state
        // The dashboard will refresh automatically via real-time subscription
      } catch (error) {
        console.error('Error saving transaction to Supabase:', error);
        // Still add to local state as fallback if Supabase fails
        onTransactionAdd?.(transaction);
      }
    } else if (transaction) {
      console.log('No user ID available, adding transaction locally only');
      onTransactionAdd?.(transaction);
    } else {
      console.log('No transaction parsed from message:', inputText);
    }
    
    setInputText('');
    setIsTyping(true);

    // Simulate ANITA typing
    setTimeout(async () => {
      const anitaResponse: Message = {
        id: (Date.now() + 1).toString(),
        text: getAnitaResponse(inputText, transaction || undefined),
        sender: 'anita',
        timestamp: new Date()
      };
      
      await onMessageAdd?.(anitaResponse);
      setIsTyping(false);
    }, 1500);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <div className="chat-interface">
      <div className="chat-header">
        <h1 className="main-title">ANITA</h1>
      </div>

      <div className="chat-messages">
        {messages.map((message) => (
          <div key={message.id} className={`message ${message.sender}`}>
            <div className="message-avatar">
              {message.sender === 'anita' ? <Bot size={20} /> : <User size={20} />}
            </div>
            <div className="message-content">
              <div className="message-text">{message.text}</div>
              {message.transaction && (
                <div className={`transaction-info ${message.transaction.type}`}>
                  <span className="transaction-amount">
                    {message.transaction.type === 'income' ? '+' : '-'}${message.transaction.amount}
                  </span>
                  <span className="transaction-desc">{message.transaction.description}</span>
                </div>
              )}
              <div className="message-time">
                {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
              </div>
            </div>
          </div>
        ))}
        
        {isTyping && (
          <div className="message anita">
            <div className="message-avatar">
              <Bot size={20} />
            </div>
            <div className="message-content">
              <div className="typing-indicator">
                <span></span>
                <span></span>
                <span></span>
              </div>
            </div>
          </div>
        )}
        
        <div ref={messagesEndRef} />
      </div>

      <div className="chat-input-container">
        <div className="chat-input">
          <textarea
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Ask ANITA about your finances..."
            rows={1}
            className="input-field"
          />
          <button 
            onClick={handleSendMessage}
            disabled={!inputText.trim() || isTyping}
            className="send-button"
          >
            <Send size={20} />
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatInterface;
