import { createClient } from '@supabase/supabase-js'

const supabaseUrl = 'https://kzregiqxflrvaxytdet.supabase.co'
const supabaseAnonKey = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImtlenJlZ2lxZnhscnZheHl0ZGV0Iiwicm9sZSI6ImFub24iLCJpYXQiOjE3NTc2OTY5MTgsImV4cCI6MjA3MzI3MjkxOH0.X4QWu0W31Kv_8KGQ6h_n4PYnQOMTX85CYbWJVbv2AxM'

export const supabase = createClient(supabaseUrl, supabaseAnonKey)

// Local database fallback
class LocalDatabase {
  constructor() {
    this.dataFile = 'anita-local-data.json';
    this.loadData();
  }

  loadData() {
    try {
      if (typeof window !== 'undefined' && window.localStorage) {
        // Browser environment
        const data = localStorage.getItem('anita-local-data');
        if (data) {
          this.data = JSON.parse(data);
        } else {
          this.data = this.getDefaultData();
          this.saveData();
        }
      } else {
        // Node environment - use default data for now
        this.data = this.getDefaultData();
      }
    } catch (error) {
      console.error('Error loading local data:', error);
      this.data = this.getDefaultData();
    }
  }

  getDefaultData() {
    return {
      users: [
        {
          id: '00000000-0000-0000-0000-000000000001',
          username: 'default_user',
          email: 'default@anita.com',
          display_name: 'Default User',
          created_at: new Date().toISOString()
        }
      ],
      transactions: [],
      messages: [
        {
          id: '1',
          user_id: '00000000-0000-0000-0000-000000000001',
          content: 'Hey! Any new expenses or incomes today?',
          message_type: 'bot',
          created_at: new Date().toISOString()
        }
      ]
    };
  }

  saveData() {
    try {
      if (typeof window !== 'undefined' && window.localStorage) {
        // Browser environment
        localStorage.setItem('anita-local-data', JSON.stringify(this.data));
      } else {
        // Node environment
        const fs = require('fs');
        fs.writeFileSync(this.dataFile, JSON.stringify(this.data, null, 2));
      }
    } catch (error) {
      console.error('Error saving local data:', error);
    }
  }

  // User operations
  async getOrCreateDefaultUser() {
    const defaultUser = this.data.users.find(u => u.username === 'default_user');
    if (defaultUser) {
      return { data: defaultUser, error: null };
    }
    
    const newUser = {
      id: '00000000-0000-0000-0000-000000000001',
      username: 'default_user',
      email: 'default@anita.com',
      display_name: 'Default User',
      created_at: new Date().toISOString()
    };
    
    this.data.users.push(newUser);
    this.saveData();
    return { data: newUser, error: null };
  }

  // Transaction operations
  async insertTransaction(transaction, userId) {
    const newTransaction = {
      id: Date.now().toString(),
      user_id: userId || '00000000-0000-0000-0000-000000000001',
      ...transaction,
      created_at: new Date().toISOString()
    };
    
    this.data.transactions.push(newTransaction);
    this.saveData();
    return { data: [newTransaction], error: null };
  }

  async fetchTransactions(userId) {
    const userTransactions = this.data.transactions.filter(
      t => t.user_id === (userId || '00000000-0000-0000-0000-000000000001')
    );
    return { data: userTransactions, error: null };
  }

  // Message operations
  async insertMessage(userId, content, messageType = 'user') {
    const newMessage = {
      id: Date.now().toString(),
      user_id: userId || '00000000-0000-0000-0000-000000000001',
      content,
      message_type: messageType,
      created_at: new Date().toISOString()
    };
    
    this.data.messages.push(newMessage);
    this.saveData();
    return { data: [newMessage], error: null };
  }

  async fetchMessages(userId) {
    const userMessages = this.data.messages.filter(
      m => m.user_id === (userId || '00000000-0000-0000-0000-000000000001')
    );
    return { data: userMessages, error: null };
  }
}

// Create local database instance
const localDb = new LocalDatabase();

// User management API
export const usersApi = {
  // Get or create a default user for demo purposes
  async getOrCreateDefaultUser() {
    try {
      // First try Supabase
      const { data: existingUser, error: fetchError } = await supabase
        .from('users')
        .select('*')
        .eq('username', 'default_user')
        .single()
      
      if (existingUser && !fetchError) {
        return { data: existingUser, error: null }
      }
      
      // If user doesn't exist, create it
      const { data: newUser, error: createError } = await supabase
        .from('users')
        .insert([{
          username: 'default_user',
          email: 'default@anita.com',
          display_name: 'Default User'
        }])
        .select()
        .single()
      
      if (createError) {
        console.log('Supabase user creation failed, using local database');
        return await localDb.getOrCreateDefaultUser();
      }
      return { data: newUser, error: null }
    } catch (error) {
      console.log('Supabase connection failed, using local database');
      return await localDb.getOrCreateDefaultUser();
    }
  }
}

// Messages table CRUD operations
export const messagesApi = {
  // Insert a new message
  async insertMessage(userId, content, messageType = 'user') {
    try {
      const { data, error } = await supabase
        .from('messages')
        .insert([
          { 
            user_id: userId || '00000000-0000-0000-0000-000000000001',
            content,
            message_type: messageType
          }
        ])
        .select()
      
      if (error) {
        console.log('Supabase message insertion failed, using local database');
        return await localDb.insertMessage(userId, content, messageType);
      }
      return { data, error: null }
    } catch (error) {
      console.log('Supabase connection failed, using local database');
      return await localDb.insertMessage(userId, content, messageType);
    }
  },

  // Fetch all messages for a user
  async fetchMessages(userId) {
    try {
      const { data, error } = await supabase
        .from('messages')
        .select('*')
        .eq('user_id', userId || '00000000-0000-0000-0000-000000000001')
        .order('created_at', { ascending: true })
      
      if (error) {
        console.log('Supabase message fetching failed, using local database');
        return await localDb.fetchMessages(userId);
      }
      return { data, error: null }
    } catch (error) {
      console.log('Supabase connection failed, using local database');
      return await localDb.fetchMessages(userId);
    }
  }
}

// Transactions table CRUD operations
export const transactionsApi = {
  // Insert a new transaction
  async insertTransaction(transaction, userId) {
    try {
      console.log('Attempting to insert transaction:', transaction);
      
      const defaultUserId = userId || '00000000-0000-0000-0000-000000000001';
      
      const insertData = {
        user_id: defaultUserId,
        type: transaction.type,
        amount: transaction.amount,
        category: transaction.category,
        description: transaction.description,
        date: transaction.date.toISOString()
      };
      
      const { data, error } = await supabase
        .from('transactions')
        .insert([insertData])
        .select()
      
      if (error) {
        console.log('Supabase transaction insertion failed, using local database');
        return await localDb.insertTransaction(transaction, userId);
      }
      console.log('Transaction inserted successfully:', data);
      return { data, error: null }
    } catch (error) {
      console.log('Supabase connection failed, using local database');
      return await localDb.insertTransaction(transaction, userId);
    }
  },

  // Fetch all transactions
  async fetchTransactions(userId) {
    try {
      const defaultUserId = userId || '00000000-0000-0000-0000-000000000001';
      
      const { data, error } = await supabase
        .from('transactions')
        .select('*')
        .eq('user_id', defaultUserId)
        .order('date', { ascending: false })
      
      if (error) {
        console.log('Supabase transaction fetching failed, using local database');
        return await localDb.fetchTransactions(userId);
      }
      return { data, error: null }
    } catch (error) {
      console.log('Supabase connection failed, using local database');
      return await localDb.fetchTransactions(userId);
    }
  }
}
