import React, { useState } from 'react';

const SimpleSupabaseTest: React.FC = () => {
  const [result, setResult] = useState<string>('');
  const [loading, setLoading] = useState(false);

  const testConnection = async () => {
    setLoading(true);
    setResult('Testing connection...\n');
    
    try {
      // Test 1: Basic fetch to Supabase
      setResult(prev => prev + '1. Testing basic connection...\n');
      
      const response = await fetch('https://kzregiqxflrvaxytdet.supabase.co/rest/v1/', {
        method: 'GET',
        headers: {
          'apikey': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImtlenJlZ2lxZnhscnZheHl0ZGV0Iiwicm9sZSI6ImFub24iLCJpYXQiOjE3NTc2OTY5MTgsImV4cCI6MjA3MzI3MjkxOH0.X4QWu0W31Kv_8KGQ6h_n4PYnQOMTX85CYbWJVbv2AxM',
          'Authorization': 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImtlenJlZ2lxZnhscnZheHl0ZGV0Iiwicm9sZSI6ImFub24iLCJpYXQiOjE3NTc2OTY5MTgsImV4cCI6MjA3MzI3MjkxOH0.X4QWu0W31Kv_8KGQ6h_n4PYnQOMTX85CYbWJVbv2AxM'
        }
      });
      
      setResult(prev => prev + `   Status: ${response.status} ${response.statusText}\n`);
      
      if (response.ok) {
        setResult(prev => prev + '   ‚úÖ Basic connection works!\n');
        
        // Test 2: Try to access a table
        setResult(prev => prev + '2. Testing table access...\n');
        
        const tableResponse = await fetch('https://kzregiqxflrvaxytdet.supabase.co/rest/v1/transactions?select=count', {
          method: 'GET',
          headers: {
            'apikey': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImtlenJlZ2lxZnhscnZheHl0ZGV0Iiwicm9sZSI6ImFub24iLCJpYXQiOjE3NTc2OTY5MTgsImV4cCI6MjA3MzI3MjkxOH0.X4QWu0W31Kv_8KGQ6h_n4PYnQOMTX85CYbWJVbv2AxM',
            'Authorization': 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImtlenJlZ2lxZnhscnZheHl0ZGV0Iiwicm9sZSI6ImFub24iLCJpYXQiOjE3NTc2OTY5MTgsImV4cCI6MjA3MzI3MjkxOH0.X4QWu0W31Kv_8KGQ6h_n4PYnQOMTX85CYbWJVbv2AxM'
          }
        });
        
        setResult(prev => prev + `   Table Status: ${tableResponse.status} ${tableResponse.statusText}\n`);
        
        if (tableResponse.ok) {
          const data = await tableResponse.json();
          setResult(prev => prev + `   ‚úÖ Table access works! Found ${data.length || 0} records\n`);
        } else {
          setResult(prev => prev + `   ‚ùå Table access failed\n`);
        }
        
      } else {
        setResult(prev => prev + `   ‚ùå Basic connection failed\n`);
      }
      
    } catch (error) {
      setResult(prev => prev + `‚ùå Error: ${error}\n`);
    }
    
    setLoading(false);
  };

  return (
    <div style={{ 
      padding: '20px', 
      margin: '20px', 
      border: '2px solid #007bff', 
      borderRadius: '8px',
      backgroundColor: '#f8f9fa'
    }}>
      <h3>üîß Simple Supabase Connection Test</h3>
      <button 
        onClick={testConnection} 
        disabled={loading}
        style={{ 
          padding: '10px 20px', 
          backgroundColor: '#007bff', 
          color: 'white', 
          border: 'none', 
          borderRadius: '4px',
          cursor: loading ? 'not-allowed' : 'pointer'
        }}
      >
        {loading ? 'Testing...' : 'Test Connection'}
      </button>
      
      {result && (
        <div style={{ 
          marginTop: '15px', 
          padding: '10px', 
          backgroundColor: '#e9ecef', 
          borderRadius: '4px',
          fontFamily: 'monospace',
          whiteSpace: 'pre-line'
        }}>
          {result}
        </div>
      )}
    </div>
  );
};

export default SimpleSupabaseTest;
