import React, { useState } from 'react';
import { transactionsApi } from '../supabase';

const SupabaseTest: React.FC = () => {
  const [testResult, setTestResult] = useState<string>('');
  const [loading, setLoading] = useState(false);

  const testConnection = async () => {
    setLoading(true);
    setTestResult('Testing Supabase connection...');
    
    try {
      // Test 1: Try to fetch transactions
      const { data: fetchData, error: fetchError } = await transactionsApi.fetchTransactions();
      
      if (fetchError) {
        setTestResult(`‚ùå Fetch Error: ${JSON.stringify(fetchError)}`);
        console.error('Fetch error:', fetchError);
      } else {
        setTestResult(`‚úÖ Fetch Success: Found ${fetchData?.length || 0} transactions`);
        console.log('Fetch data:', fetchData);
      }
      
      // Test 2: Try to insert a test transaction
      const testTransaction = {
        id: Date.now().toString(),
        type: 'expense' as const,
        amount: 10.50,
        category: 'Test',
        description: 'Test transaction from app',
        date: new Date()
      };
      
      console.log('Attempting to insert test transaction:', testTransaction);
      const { data: insertData, error: insertError } = await transactionsApi.insertTransaction(testTransaction);
      
      if (insertError) {
        setTestResult(prev => prev + `\n‚ùå Insert Error: ${JSON.stringify(insertError)}`);
        console.error('Insert error:', insertError);
      } else {
        setTestResult(prev => prev + `\n‚úÖ Insert Success: Transaction saved with ID ${insertData?.[0]?.id}`);
        console.log('Insert data:', insertData);
      }
      
    } catch (error) {
      setTestResult(`‚ùå Connection Error: ${error}`);
      console.error('Connection error:', error);
    }
    
    setLoading(false);
  };

  return (
    <div style={{ 
      padding: '20px', 
      margin: '20px', 
      borderRadius: '12px',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      color: 'white',
      boxShadow: '0 8px 32px rgba(0,0,0,0.3)',
      border: '2px solid #fff',
      position: 'relative',
      overflow: 'hidden'
    }}>
      {/* Decorative elements */}
      <div style={{
        position: 'absolute',
        top: '-50px',
        right: '-50px',
        width: '100px',
        height: '100px',
        background: 'rgba(255,255,255,0.1)',
        borderRadius: '50%'
      }}></div>
      <div style={{
        position: 'absolute',
        bottom: '-30px',
        left: '-30px',
        width: '60px',
        height: '60px',
        background: 'rgba(255,255,255,0.1)',
        borderRadius: '50%'
      }}></div>
      
      <div style={{ position: 'relative', zIndex: 1 }}>
        <h2 style={{ 
          margin: '0 0 15px 0', 
          fontSize: '24px',
          fontWeight: 'bold',
          textShadow: '2px 2px 4px rgba(0,0,0,0.3)'
        }}>
          üîß Supabase Connection Test
        </h2>
        
        <p style={{ 
          margin: '0 0 20px 0', 
          fontSize: '16px',
          opacity: 0.9
        }}>
          Test if your app can connect to the database
        </p>
        
        <button 
          onClick={testConnection} 
          disabled={loading}
          style={{ 
            padding: '15px 30px', 
            marginBottom: '20px',
            fontSize: '16px',
            fontWeight: 'bold',
            backgroundColor: loading ? '#ccc' : '#ff6b6b',
            color: 'white',
            border: 'none',
            borderRadius: '8px',
            cursor: loading ? 'not-allowed' : 'pointer',
            boxShadow: '0 4px 15px rgba(0,0,0,0.2)',
            transition: 'all 0.3s ease',
            transform: loading ? 'none' : 'translateY(0)',
          }}
          onMouseOver={(e) => {
            if (!loading) {
              e.currentTarget.style.transform = 'translateY(-2px)';
              e.currentTarget.style.boxShadow = '0 6px 20px rgba(0,0,0,0.3)';
            }
          }}
          onMouseOut={(e) => {
            if (!loading) {
              e.currentTarget.style.transform = 'translateY(0)';
              e.currentTarget.style.boxShadow = '0 4px 15px rgba(0,0,0,0.2)';
            }
          }}
        >
          {loading ? 'üîÑ Testing...' : 'üöÄ Test Database Connection'}
        </button>
        
        {testResult && (
          <div style={{ 
            backgroundColor: 'rgba(0,0,0,0.2)',
            padding: '15px',
            borderRadius: '8px',
            border: '1px solid rgba(255,255,255,0.2)',
            whiteSpace: 'pre-line', 
            fontFamily: 'monospace', 
            fontSize: '14px',
            lineHeight: '1.4'
          }}>
            {testResult}
          </div>
        )}
      </div>
    </div>
  );
};

export default SupabaseTest;
