import React, { useState, useEffect, useCallback } from 'react';
import Sidebar from './components/Sidebar';
import ChatInterface from './components/ChatInterface';
import Settings from './components/Settings';
import FinancePage from './components/FinancePage';
import socketService, { Transaction, Message, User } from './socketService';
import './App.css';

type ViewType = 'chat' | 'settings' | 'finance';
type Theme = 'dark' | 'light';

const App: React.FC = () => {
  const [currentView, setCurrentView] = useState<ViewType>('chat');
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [messages, setMessages] = useState<Message[]>([
    {
      id: '1',
      text: "Hey! Any new expenses or incomes today?",
      sender: 'anita',
      timestamp: new Date()
    }
  ]);
  const [theme, setTheme] = useState<Theme>('dark');
  const [currentUserId, setCurrentUserId] = useState<string | null>(null);
  const [isConnected, setIsConnected] = useState(false);

  // Initialize Socket.IO connection
  useEffect(() => {
    const initializeSocket = async () => {
      try {
        await socketService.connect();
        console.log('✅ Socket.IO connected successfully');
      } catch (error) {
        console.error('❌ Failed to connect to Socket.IO server:', error);
        // Fallback to localStorage
        loadFromLocalStorage();
      }
    };

    initializeSocket();

    // Set up event listeners
    socketService.onConnectionChange((connected) => {
      setIsConnected(connected);
      if (connected) {
        console.log('🔄 Socket.IO reconnected, refreshing data...');
        socketService.refreshData();
      }
    });

    socketService.onDataRefreshed((data) => {
      console.log('📊 Data refreshed from server:', data);
      setTransactions(data.transactions || []);
      setMessages(data.messages || []);
      
      // Set current user if available
      if (data.users && data.users.length > 0) {
        setCurrentUserId(data.users[0].id);
      }
    });

    socketService.onTransactionAdded((transaction) => {
      setTransactions(prev => [...prev, transaction]);
    });

    socketService.onTransactionUpdated((transaction) => {
      setTransactions(prev => 
        prev.map(t => t.id === transaction.id ? transaction : t)
      );
    });

    socketService.onTransactionDeleted((transactionId) => {
      setTransactions(prev => prev.filter(t => t.id !== transactionId));
    });

    socketService.onMessageAdded((message) => {
      setMessages(prev => [...prev, message]);
    });

    socketService.onUserUpdated((user) => {
      setCurrentUserId(user.id);
    });

    return () => {
      socketService.disconnect();
    };
  }, []);

  // Load data from localStorage as fallback
  const loadFromLocalStorage = () => {
    console.log('📱 Loading data from localStorage fallback...');
    
    // Load theme
    const savedTheme = localStorage.getItem('anita-theme') as Theme;
    if (savedTheme) {
      setTheme(savedTheme);
    }

    // Load transactions
    const savedTransactions = localStorage.getItem('anita-transactions');
    if (savedTransactions) {
      try {
        const parsed = JSON.parse(savedTransactions);
        const transactionsWithDates = parsed.map((t: any) => ({
          ...t,
          date: new Date(t.date)
        }));
        setTransactions(transactionsWithDates);
        console.log('📱 Loaded transactions from localStorage');
      } catch (error) {
        console.error('Error loading transactions from localStorage:', error);
      }
    }

    // Load messages
    const savedMessages = localStorage.getItem('anita-messages');
    if (savedMessages) {
      try {
        const parsed = JSON.parse(savedMessages);
        const messagesWithDates = parsed.map((m: any) => ({
          ...m,
          timestamp: new Date(m.timestamp)
        }));
        setMessages(messagesWithDates);
        console.log('📱 Loaded messages from localStorage');
      } catch (error) {
        console.error('Error loading messages from localStorage:', error);
      }
    }

    // Create default user
    const defaultUser: User = {
      id: 'default-user',
      username: 'User',
      createdAt: new Date()
    };
    setCurrentUserId(defaultUser.id);
  };

  // Apply theme to document
  useEffect(() => {
    document.documentElement.setAttribute('data-theme', theme);
    localStorage.setItem('anita-theme', theme);
  }, [theme]);

  const handleTransactionAdd = (transaction: Transaction) => {
    const newTransactions = [...transactions, transaction];
    setTransactions(newTransactions);
    // Save to localStorage as backup
    localStorage.setItem('anita-transactions', JSON.stringify(newTransactions));
    console.log('✅ Transaction added to local state:', transaction);
  };

  const handleMessageAdd = (message: Message) => {
    const newMessages = [...messages, message];
    setMessages(newMessages);
    // Save to localStorage
    localStorage.setItem('anita-messages', JSON.stringify(newMessages));
  };

  const handleThemeChange = (newTheme: Theme) => {
    setTheme(newTheme);
  };

  // Calculate financial summary
  const totalIncome = transactions
    .filter(t => t.type === 'income')
    .reduce((sum, t) => sum + t.amount, 0);
  
  const totalExpenses = transactions
    .filter(t => t.type === 'expense')
    .reduce((sum, t) => sum + t.amount, 0);
  
  const netBalance = totalIncome - totalExpenses;
  
  // Get all transactions sorted by date (newest first)
  const allTransactions = [...transactions]
    .sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime());

  return (
    <div className="app">
      <Sidebar 
        onViewChange={setCurrentView} 
        currentView={currentView}
        totalBalance={netBalance}
        monthlyIncome={totalIncome}
        monthlyExpenses={totalExpenses}
        recentTransactions={allTransactions}
      />
      <div className="main-content">
        {currentView === 'chat' && (
          <ChatInterface 
            onTransactionAdd={handleTransactionAdd} 
            onMessageAdd={handleMessageAdd} 
            messages={messages}
            currentUserId={currentUserId}
          />
        )}
        {currentView === 'settings' && <Settings onThemeChange={handleThemeChange} currentTheme={theme} />}
        {currentView === 'finance' && <FinancePage transactions={transactions} />}
      </div>
    </div>
  );
};

export default App;
