import React from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer, BarChart, Bar, XAxis, YAxis, Tooltip } from 'recharts';
import './FinancialStats.css';

interface Transaction {
  id: string;
  type: 'income' | 'expense';
  amount: number;
  category: string;
  description: string;
  date: Date;
}

interface FinancialStatsProps {
  transactions?: Transaction[];
}

const FinancialStats: React.FC<FinancialStatsProps> = ({ transactions = [] }) => {
  // Calculate real data from transactions
  const currentMonth = new Date().getMonth();
  const currentYear = new Date().getFullYear();
  
  const currentMonthTransactions = transactions.filter(t => {
    const transactionDate = new Date(t.date);
    return transactionDate.getMonth() === currentMonth && transactionDate.getFullYear() === currentYear;
  });

  const income = currentMonthTransactions.filter(t => t.type === 'income');
  const expenses = currentMonthTransactions.filter(t => t.type === 'expense');

  // Calculate expense categories
  const expenseCategories = expenses.reduce((acc, transaction) => {
    const category = transaction.category || 'Other';
    if (!acc[category]) {
      acc[category] = 0;
    }
    acc[category] += transaction.amount;
    return acc;
  }, {} as Record<string, number>);

  const expenseData = Object.entries(expenseCategories).map(([name, value], index) => ({
    name,
    value,
    color: ['#667eea', '#764ba2', '#f093fb', '#f5576c', '#4facfe', '#10b981', '#f59e0b'][index % 7]
  }));

  // Calculate monthly data for the last 6 months
  const monthlyData = [];
  for (let i = 5; i >= 0; i--) {
    const date = new Date();
    date.setMonth(date.getMonth() - i);
    const monthName = date.toLocaleDateString('en-US', { month: 'short' });
    
    const monthTransactions = transactions.filter(t => {
      const transactionDate = new Date(t.date);
      return transactionDate.getMonth() === date.getMonth() && 
             transactionDate.getFullYear() === date.getFullYear();
    });
    
    const monthIncome = monthTransactions
      .filter(t => t.type === 'income')
      .reduce((sum, t) => sum + t.amount, 0);
    
    const monthExpenses = monthTransactions
      .filter(t => t.type === 'expense')
      .reduce((sum, t) => sum + t.amount, 0);
    
    monthlyData.push({
      month: monthName,
      income: monthIncome,
      expenses: monthExpenses
    });
  }

  const totalIncome = income.reduce((sum, t) => sum + t.amount, 0);
  const totalExpenses = expenses.reduce((sum, t) => sum + t.amount, 0);
  const savingsRate = totalIncome > 0 ? Math.round(((totalIncome - totalExpenses) / totalIncome) * 100) : 0;

  return (
    <div className="financial-stats">
      <div className="stats-section">
        <h4>Monthly Expenses</h4>
        <div className="pie-chart-container">
          <ResponsiveContainer width="100%" height={150}>
            <PieChart>
              <Pie
                data={expenseData}
                cx="50%"
                cy="50%"
                innerRadius={40}
                outerRadius={60}
                paddingAngle={2}
                dataKey="value"
              >
                {expenseData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={entry.color} />
                ))}
              </Pie>
              <Tooltip 
                formatter={(value: number) => [`$${value}`, 'Amount']}
                contentStyle={{
                  backgroundColor: '#2a2a2a',
                  border: '1px solid #444',
                  borderRadius: '8px',
                  color: '#fff'
                }}
              />
            </PieChart>
          </ResponsiveContainer>
        </div>
        <div className="expense-legend">
          {expenseData.map((item, index) => (
            <div key={index} className="legend-item">
              <div 
                className="legend-color" 
                style={{ backgroundColor: item.color }}
              />
              <span className="legend-label">{item.name}</span>
              <span className="legend-value">${item.value}</span>
            </div>
          ))}
        </div>
      </div>

      <div className="stats-section">
        <h4>Income vs Expenses</h4>
        <div className="bar-chart-container">
          <ResponsiveContainer width="100%" height={120}>
            <BarChart data={monthlyData}>
              <XAxis 
                dataKey="month" 
                axisLine={false}
                tickLine={false}
                tick={{ fill: '#888', fontSize: 12 }}
              />
              <YAxis 
                axisLine={false}
                tickLine={false}
                tick={{ fill: '#888', fontSize: 12 }}
                tickFormatter={(value) => `$${value/1000}k`}
              />
              <Tooltip 
                formatter={(value: number, name: string) => [
                  `$${value}`, 
                  name === 'income' ? 'Income' : 'Expenses'
                ]}
                contentStyle={{
                  backgroundColor: '#2a2a2a',
                  border: '1px solid #444',
                  borderRadius: '8px',
                  color: '#fff'
                }}
              />
              <Bar dataKey="income" fill="#10b981" radius={[2, 2, 0, 0]} />
              <Bar dataKey="expenses" fill="#ef4444" radius={[2, 2, 0, 0]} />
            </BarChart>
          </ResponsiveContainer>
        </div>
      </div>

      <div className="stats-summary">
        <div className="summary-item">
          <span className="summary-label">Total Monthly Expenses</span>
          <span className="summary-value">${totalExpenses}</span>
        </div>
        <div className="summary-item">
          <span className="summary-label">Savings Rate</span>
          <span className="summary-value positive">23%</span>
        </div>
      </div>
    </div>
  );
};

export default FinancialStats;
