import React, { useState, useEffect } from 'react'
import { messagesApi } from '../supabase'
import './Messages.css'

interface Message {
  id: number
  user: string
  content: string
  created_at: string
}

const Messages: React.FC = () => {
  const [messages, setMessages] = useState<Message[]>([])
  const [newMessage, setNewMessage] = useState({ user: '', content: '' })
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState<string | null>(null)

  // Fetch messages on component mount
  useEffect(() => {
    fetchMessages()
  }, [])

  const fetchMessages = async () => {
    setLoading(true)
    setError(null)
    
    const { data, error } = await messagesApi.fetchMessages()
    
    if (error) {
      setError('Failed to fetch messages')
      console.error('Error:', error)
    } else {
      setMessages(data || [])
    }
    
    setLoading(false)
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    
    if (!newMessage.user.trim() || !newMessage.content.trim()) {
      setError('Please fill in both user and content fields')
      return
    }

    setLoading(true)
    setError(null)

    const { data, error } = await messagesApi.insertMessage(
      newMessage.user.trim(),
      newMessage.content.trim()
    )

    if (error) {
      setError('Failed to add message')
      console.error('Error:', error)
    } else {
      // Add the new message to the local state
      if (data && data[0]) {
        setMessages(prev => [...prev, data[0]])
      }
      // Reset form
      setNewMessage({ user: '', content: '' })
    }

    setLoading(false)
  }

  const handleDelete = async (id: number) => {
    if (!window.confirm('Are you sure you want to delete this message?')) {
      return
    }

    setLoading(true)
    setError(null)

    const { error } = await messagesApi.deleteMessage(id)

    if (error) {
      setError('Failed to delete message')
      console.error('Error:', error)
    } else {
      // Remove the message from local state
      setMessages(prev => prev.filter(msg => msg.id !== id))
    }

    setLoading(false)
  }

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleString()
  }

  return (
    <div className="messages-container">
      <div className="messages-header">
        <h2>Messages</h2>
        <button 
          onClick={fetchMessages} 
          disabled={loading}
          className="refresh-btn"
        >
          {loading ? 'Loading...' : 'Refresh'}
        </button>
      </div>

      {error && (
        <div className="error-message">
          {error}
        </div>
      )}

      <form onSubmit={handleSubmit} className="message-form">
        <div className="form-group">
          <input
            type="text"
            placeholder="User name"
            value={newMessage.user}
            onChange={(e) => setNewMessage(prev => ({ ...prev, user: e.target.value }))}
            disabled={loading}
            className="form-input"
          />
        </div>
        <div className="form-group">
          <textarea
            placeholder="Message content"
            value={newMessage.content}
            onChange={(e) => setNewMessage(prev => ({ ...prev, content: e.target.value }))}
            disabled={loading}
            className="form-textarea"
            rows={3}
          />
        </div>
        <button 
          type="submit" 
          disabled={loading || !newMessage.user.trim() || !newMessage.content.trim()}
          className="submit-btn"
        >
          {loading ? 'Adding...' : 'Add Message'}
        </button>
      </form>

      <div className="messages-list">
        {messages.length === 0 ? (
          <div className="no-messages">
            {loading ? 'Loading messages...' : 'No messages yet. Add one above!'}
          </div>
        ) : (
          messages.map((message) => (
            <div key={message.id} className="message-item">
              <div className="message-header">
                <span className="message-user">{message.user}</span>
                <span className="message-date">{formatDate(message.created_at)}</span>
                <button
                  onClick={() => handleDelete(message.id)}
                  disabled={loading}
                  className="delete-btn"
                  title="Delete message"
                >
                  ×
                </button>
              </div>
              <div className="message-content">{message.content}</div>
            </div>
          ))
        )}
      </div>
    </div>
  )
}

export default Messages
