import React from 'react';
import { 
  MessageCircle, 
  Settings, 
  TrendingUp, 
  DollarSign,
  BarChart3
} from 'lucide-react';
import './Sidebar.css';

interface SidebarProps {
  onViewChange: (view: 'chat' | 'settings' | 'finance') => void;
  currentView: 'chat' | 'settings' | 'finance';
}

const Sidebar: React.FC<SidebarProps> = ({ onViewChange, currentView }) => {
  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <div className="logo">
          <div className="logo-icon">A</div>
          <span className="logo-text">ANITA</span>
        </div>
        <p className="tagline">Your Finance AI Assistant</p>
      </div>

      <div className="sidebar-nav">
        <button 
          className={`nav-item ${currentView === 'chat' ? 'active' : ''}`}
          onClick={() => onViewChange('chat')}
        >
          <MessageCircle size={20} />
          <span>Chat with ANITA</span>
        </button>
        <button 
          className={`nav-item ${currentView === 'finance' ? 'active' : ''}`}
          onClick={() => onViewChange('finance')}
        >
          <BarChart3 size={20} />
          <span>Finance Dashboard</span>
        </button>
        <button 
          className={`nav-item ${currentView === 'settings' ? 'active' : ''}`}
          onClick={() => onViewChange('settings')}
        >
          <Settings size={20} />
          <span>Settings</span>
        </button>
      </div>

      <div className="sidebar-footer">
        <div className="quick-stats">
          <div className="quick-stat">
            <DollarSign size={16} />
            <span>Total Balance</span>
            <span className="stat-value">$12,450</span>
          </div>
          <div className="quick-stat">
            <TrendingUp size={16} />
            <span>This Month</span>
            <span className="stat-value positive">+$1,200</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
