import React, { useState, useEffect } from 'react';
import Sidebar from './components/Sidebar';
import ChatInterface from './components/ChatInterface';
import Settings from './components/Settings';
import FinancePage from './components/FinancePage';
import { transactionsApi } from './supabase';
import './App.css';

type ViewType = 'chat' | 'settings' | 'finance';
type Theme = 'dark' | 'light';

interface Transaction {
  id: string;
  type: 'income' | 'expense';
  amount: number;
  category: string;
  description: string;
  date: Date;
  userId?: string;
  username?: string;
}

interface Message {
  id: string;
  text: string;
  sender: 'user' | 'anita';
  timestamp: Date;
  transaction?: {
    type: 'income' | 'expense';
    amount: number;
    category: string;
    description: string;
  };
  userId?: string;
  username?: string;
}

interface User {
  id: string;
  username: string;
  display_name: string;
  email?: string;
  created_at: string;
}

const App: React.FC = () => {
  const [currentView, setCurrentView] = useState<ViewType>('chat');
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [messages, setMessages] = useState<Message[]>([]);
  const [users, setUsers] = useState<User[]>([]);
  const [currentUser, setCurrentUser] = useState<User | null>(null);
  const [theme, setTheme] = useState<Theme>('dark');
  const [isLoading, setIsLoading] = useState(true);

  // Initialize user and load data
  useEffect(() => {
    const initializeApp = async () => {
      try {
        setIsLoading(true);
        
        // Load theme from localStorage
        const savedTheme = localStorage.getItem('anita-theme') as Theme;
        if (savedTheme) {
          setTheme(savedTheme);
        }

        // Get or create current user
        const username = localStorage.getItem('anita-username') || 'anonymous_user';
        const { data: user, error: userError } = await usersApi.getOrCreateUser(username);
        
        if (user && !userError) {
          setCurrentUser(user);
          console.log('âœ… Current user:', user);
        } else {
          console.error('âŒ Error getting/creating user:', userError);
        }

        // Load all users
        const { data: allUsers, error: usersError } = await usersApi.getAllUsers();
        if (allUsers && !usersError) {
          setUsers(allUsers);
          console.log('âœ… Loaded users:', allUsers);
        }

        // Load transactions
        const { data: transactionsData, error: transactionsError } = await transactionsApi.fetchTransactions();
        if (transactionsData && !transactionsError) {
          const transactionsWithDates = transactionsData.map((t: any) => ({
            id: t.id.toString(),
            type: t.type,
            amount: parseFloat(t.amount),
            category: t.category,
            description: t.description,
            date: new Date(t.date),
            userId: t.user_id,
            username: t.users?.username || 'Unknown'
          }));
          setTransactions(transactionsWithDates);
          console.log('âœ… Loaded transactions:', transactionsWithDates);
        }

        // Load messages
        let messagesWithDates: Message[] = [];
        const { data: messagesData, error: messagesError } = await messagesApi.fetchMessages();
        if (messagesData && !messagesError) {
          messagesWithDates = messagesData.map((m: any) => ({
            id: m.id.toString(),
            text: m.content,
            sender: (m.message_type === 'user' ? 'user' : 'anita') as 'user' | 'anita',
            timestamp: new Date(m.created_at),
            userId: m.user_id,
            username: m.users?.username || 'Unknown'
          }));
          setMessages(messagesWithDates);
          console.log('âœ… Loaded messages:', messagesWithDates);
        }

        // Add welcome message if no messages exist
        if (messagesWithDates.length === 0) {
          const welcomeMessage: Message = {
            id: 'welcome',
            text: `Hello ${user?.display_name || 'there'}! I'm ANITA, your finance advisor. Tell me about your income and expenses!`,
            sender: 'anita',
            timestamp: new Date(),
            userId: user?.id,
            username: user?.username
          };
          setMessages([welcomeMessage]);
        }

      } catch (error) {
        console.error('âŒ Error initializing app:', error);
      } finally {
        setIsLoading(false);
      }
    };

    initializeApp();
  }, []);

  // Set up real-time subscriptions
  useEffect(() => {
    if (!currentUser) return;

    console.log('ðŸ”„ Setting up real-time subscriptions...');

    // Subscribe to all data changes
    realtimeManager.subscribeToAll({
      onUsersChange: (payload: any) => {
        console.log('ðŸ‘¥ Users changed:', payload);
        if (payload.eventType === 'INSERT' || payload.eventType === 'UPDATE') {
          // Refresh users list
          usersApi.getAllUsers().then(({ data, error }) => {
            if (data && !error) {
              setUsers(data);
            }
          });
        }
      },
      onTransactionsChange: (payload: any) => {
        console.log('ðŸ’° Transactions changed:', payload);
        if (payload.eventType === 'INSERT' || payload.eventType === 'UPDATE' || payload.eventType === 'DELETE') {
          // Refresh transactions list
          transactionsApi.fetchTransactions().then(({ data, error }) => {
            if (data && !error) {
              const transactionsWithDates = data.map((t: any) => ({
                id: t.id.toString(),
                type: t.type,
                amount: parseFloat(t.amount),
                category: t.category,
                description: t.description,
                date: new Date(t.date),
                userId: t.user_id,
                username: t.users?.username || 'Unknown'
              }));
              setTransactions(transactionsWithDates);
            }
          });
        }
      },
      onMessagesChange: (payload: any) => {
        console.log('ðŸ’¬ Messages changed:', payload);
        if (payload.eventType === 'INSERT' || payload.eventType === 'UPDATE' || payload.eventType === 'DELETE') {
          // Refresh messages list
          messagesApi.fetchMessages().then(({ data, error }) => {
            if (data && !error) {
              const messagesWithDates = data.map((m: any) => ({
                id: m.id.toString(),
                text: m.content,
                sender: (m.message_type === 'user' ? 'user' : 'anita') as 'user' | 'anita',
                timestamp: new Date(m.created_at),
                userId: m.user_id,
                username: m.users?.username || 'Unknown'
              }));
              setMessages(messagesWithDates);
            }
          });
        }
      }
    });

    // Cleanup subscriptions on unmount
    return () => {
      realtimeManager.unsubscribeAll();
    };
  }, [currentUser]);

  // Apply theme to document
  useEffect(() => {
    document.documentElement.setAttribute('data-theme', theme);
    localStorage.setItem('anita-theme', theme);
  }, [theme]);

  const handleTransactionAdd = async (transaction: Transaction) => {
    // Transaction is already saved by ChatInterface, this is just for local state updates
    // Real-time updates will handle the UI refresh
    console.log('âœ… Transaction added to local state:', transaction);
  };

  const handleMessageAdd = async (message: Message) => {
    if (!currentUser) return;

    try {
      const { data, error } = await messagesApi.insertMessage(
        currentUser.id,
        message.text,
        message.sender === 'user' ? 'user' : 'bot'
      );

      if (error) {
        console.error('âŒ Error adding message:', error);
        return;
      }

      console.log('âœ… Message added:', data);
    } catch (error) {
      console.error('âŒ Error adding message:', error);
    }
  };

  const handleThemeChange = (newTheme: Theme) => {
    setTheme(newTheme);
  };

  const handleUserChange = async (username: string) => {
    try {
      const { data: user, error } = await usersApi.getOrCreateUser(username);
      
      if (user && !error) {
        setCurrentUser(user);
        localStorage.setItem('anita-username', username);
        console.log('âœ… Switched to user:', user);
      } else {
        console.error('âŒ Error switching user:', error);
      }
    } catch (error) {
      console.error('âŒ Error switching user:', error);
    }
  };

  // Calculate financial summary
  const totalIncome = transactions
    .filter(t => t.type === 'income')
    .reduce((sum, t) => sum + t.amount, 0);
  
  const totalExpenses = transactions
    .filter(t => t.type === 'expense')
    .reduce((sum, t) => sum + t.amount, 0);
  
  const netBalance = totalIncome - totalExpenses;

  // Get all transactions sorted by date (newest first)
  const allTransactions = [...transactions]
    .sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime());

  if (isLoading) {
    return (
      <div className="app loading">
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>Loading ANITA Finance Advisor...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="app">
      <Sidebar 
        onViewChange={setCurrentView} 
        currentView={currentView}
        totalBalance={netBalance}
        monthlyIncome={totalIncome}
        monthlyExpenses={totalExpenses}
        recentTransactions={allTransactions}
        currentUser={currentUser}
        users={users}
        onUserChange={handleUserChange}
      />
      <div className="main-content">
        {currentView === 'chat' && (
          <ChatInterface 
            onTransactionAdd={handleTransactionAdd} 
            onMessageAdd={handleMessageAdd} 
            messages={messages}
            currentUser={currentUser}
          />
        )}
        {currentView === 'settings' && (
          <Settings 
            onThemeChange={handleThemeChange} 
            currentTheme={theme}
            currentUser={currentUser}
            onUserChange={handleUserChange}
          />
        )}
        {currentView === 'finance' && (
          <FinancePage 
            transactions={transactions}
            users={users}
            currentUser={currentUser}
          />
        )}
      </div>
    </div>
  );
};

export default App;
