import React, { useState, useRef, useEffect } from 'react';
import { Send, Bot, User } from 'lucide-react';
import './ChatInterface.css';

interface Message {
  id: string;
  text: string;
  sender: 'user' | 'anita';
  timestamp: Date;
}

const ChatInterface: React.FC = () => {
  const [messages, setMessages] = useState<Message[]>([
    {
      id: '1',
      text: "Hey there! I'm ANITA, your sassy finance AI! ðŸ’… Ready to spill some financial tea and help you get your money right? What's on your mind, money honey?",
      sender: 'anita',
      timestamp: new Date()
    }
  ]);
  const [inputText, setInputText] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const getAnitaResponse = (userMessage: string): string => {
    const responses = [
      "Oh honey, let me tell you something about that budget! ðŸ’¸ It's like a good skincare routine - you gotta stick to it or everything falls apart!",
      "Listen up, money magnet! ðŸ’° That investment strategy needs more spice than your morning coffee! Let's make it work!",
      "Girl, your spending habits are giving me life! But maybe we can channel that energy into something that actually makes you money? ðŸ’…",
      "Okay, let's be real for a second - your savings account is looking drier than my humor! But don't worry, ANITA's got you! ðŸ˜‚",
      "Honey, that financial plan needs more structure than my hair on a humid day! Let's fix this together! ðŸ’ª",
      "Listen, I've seen better financial decisions in a telenovela! But that's why I'm here - to make you the main character of your own success story! âœ¨",
      "Oh sweetie, your money management is giving me secondhand stress! But don't worry, we're about to turn this ship around! ðŸš¢",
      "Babe, your budget is more chaotic than my dating life! But unlike my love life, this is fixable! Let's do this! ðŸ’•"
    ];
    
    return responses[Math.floor(Math.random() * responses.length)];
  };

  const handleSendMessage = async () => {
    if (!inputText.trim()) return;

    const userMessage: Message = {
      id: Date.now().toString(),
      text: inputText,
      sender: 'user',
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputText('');
    setIsTyping(true);

    // Simulate ANITA typing
    setTimeout(() => {
      const anitaResponse: Message = {
        id: (Date.now() + 1).toString(),
        text: getAnitaResponse(inputText),
        sender: 'anita',
        timestamp: new Date()
      };
      
      setMessages(prev => [...prev, anitaResponse]);
      setIsTyping(false);
    }, 1500);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <div className="chat-interface">
      <div className="chat-header">
        <div className="chat-title">
          <Bot size={24} />
          <h2>ANITA - Your Finance Assistant</h2>
        </div>
        <p className="chat-subtitle">Ask me anything about your finances! I'm here to help with sass and style! ðŸ’…</p>
      </div>

      <div className="chat-messages">
        {messages.map((message) => (
          <div key={message.id} className={`message ${message.sender}`}>
            <div className="message-avatar">
              {message.sender === 'anita' ? <Bot size={20} /> : <User size={20} />}
            </div>
            <div className="message-content">
              <div className="message-text">{message.text}</div>
              <div className="message-time">
                {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
              </div>
            </div>
          </div>
        ))}
        
        {isTyping && (
          <div className="message anita">
            <div className="message-avatar">
              <Bot size={20} />
            </div>
            <div className="message-content">
              <div className="typing-indicator">
                <span></span>
                <span></span>
                <span></span>
              </div>
            </div>
          </div>
        )}
        
        <div ref={messagesEndRef} />
      </div>

      <div className="chat-input-container">
        <div className="chat-input">
          <textarea
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Ask ANITA about your finances..."
            rows={1}
            className="input-field"
          />
          <button 
            onClick={handleSendMessage}
            disabled={!inputText.trim() || isTyping}
            className="send-button"
          >
            <Send size={20} />
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatInterface;
