import React, { useState, useEffect, useCallback } from 'react';
import Sidebar from './components/Sidebar';
import ChatInterface from './components/ChatInterface';
import Settings from './components/Settings';
import FinancePage from './components/FinancePage';
import { fetchTransactions, fetchMessages, saveMessage, getOrCreateDefaultUser, subscribeToTransactions } from './supabaseClient';
import './MessageRecorder.js';
import './App.css';

type ViewType = 'chat' | 'settings' | 'finance';
type Theme = 'dark' | 'light';

interface Transaction {
  id: string;
  type: 'income' | 'expense';
  amount: number;
  category: string;
  description: string;
  date: Date;
}

interface Message {
  id: string;
  text: string;
  sender: 'user' | 'anita';
  timestamp: Date;
  transaction?: {
    type: 'income' | 'expense';
    amount: number;
    category: string;
    description: string;
  };
}

const App: React.FC = () => {
  const [currentView, setCurrentView] = useState<ViewType>('chat');
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [messages, setMessages] = useState<Message[]>([
    {
      id: '1',
      text: "Hey! Any new expenses or incomes today?",
      sender: 'anita',
      timestamp: new Date()
    }
  ]);
  const [theme, setTheme] = useState<Theme>('dark');
  const [currentUserId, setCurrentUserId] = useState<string | null>(null);

  // Load theme, transactions, and messages on mount
  useEffect(() => {
    const loadData = async () => {
      // Load theme from localStorage
      const savedTheme = localStorage.getItem('anita-theme') as Theme;
      if (savedTheme) {
        setTheme(savedTheme);
      }

      // Get or create default user
      try {
        console.log('ðŸ”„ Getting/creating default user...');
        const { data: user, error: userError } = await getOrCreateDefaultUser();
        if (userError) {
          console.error('âŒ Error getting/creating user:', userError);
        } else {
          setCurrentUserId(user.id);
          console.log('âœ… User ready:', user.username);
        }
      } catch (error) {
        console.error('âŒ Error with user management:', error);
      }

      // Load transactions from Supabase (will be loaded after user is set)
      // This will be handled in the useEffect that depends on currentUserId

      // Load messages from Supabase (will be loaded after user is set)
      // This will be handled in the useEffect that depends on currentUserId
    };

    loadData();
  }, []);

  // Load transactions and messages when user ID is available
  useEffect(() => {
    if (!currentUserId) return;

    const loadUserData = async () => {
      // Load transactions from Supabase
      try {
        console.log('ðŸ”„ Loading transactions from Supabase...');
        const data = await fetchTransactions(currentUserId);
        
        // Convert Supabase data to local format
        const transactionsWithDates = data.map((t: any) => ({
          id: t.id.toString(),
          type: t.type,
          amount: parseFloat(t.amount),
          category: t.category,
          description: t.description,
          date: new Date(t.created_at)
        }));
        setTransactions(transactionsWithDates);
        console.log(`âœ… Loaded ${transactionsWithDates.length} transactions from Supabase`);
      } catch (error) {
        console.error('âŒ Error loading transactions:', error);
        // Fallback to localStorage
        const savedTransactions = localStorage.getItem('anita-transactions');
        if (savedTransactions) {
          try {
            const parsed = JSON.parse(savedTransactions);
            const transactionsWithDates = parsed.map((t: any) => ({
              ...t,
              date: new Date(t.date)
            }));
            setTransactions(transactionsWithDates);
            console.log('ðŸ“± Loaded transactions from localStorage fallback');
          } catch (error) {
            console.error('Error loading transactions from localStorage:', error);
          }
        }
      }

      // Load messages from Supabase
      try {
        console.log('ðŸ”„ Loading messages from Supabase...');
        const data = await fetchMessages(currentUserId);
        
        // Convert Supabase data to local format
        const messagesWithDates = data.map((m: any) => ({
          id: m.id.toString(),
          text: m.content,
          sender: (m.message_type === 'bot' ? 'anita' : 'user') as 'user' | 'anita',
          timestamp: new Date(m.created_at)
        }));
        setMessages(messagesWithDates);
        console.log(`âœ… Loaded ${messagesWithDates.length} messages from Supabase`);
      } catch (error) {
        console.error('âŒ Error loading messages:', error);
        // Fallback to localStorage
        const savedMessages = localStorage.getItem('anita-messages');
        if (savedMessages) {
          try {
            const parsed = JSON.parse(savedMessages);
            const messagesWithDates = parsed.map((m: any) => ({
              ...m,
              timestamp: new Date(m.timestamp)
            }));
            setMessages(messagesWithDates);
            console.log('ðŸ“± Loaded messages from localStorage fallback');
          } catch (error) {
            console.error('Error loading messages from localStorage:', error);
          }
        }
      }
    };

    loadUserData();
  }, [currentUserId]);

  // Function to refresh transactions from Supabase
  const refreshTransactionsFromSupabase = useCallback(async () => {
    if (!currentUserId) return; // Don't refresh if no user ID
    
    try {
      console.log('ðŸ”„ Refreshing transactions from Supabase...');
      const data = await fetchTransactions(currentUserId);
      
      // Convert Supabase data to local format
      const transactionsWithDates = data.map((t: any) => ({
        id: t.id.toString(),
        type: t.type,
        amount: parseFloat(t.amount),
        category: t.category,
        description: t.description,
        date: new Date(t.created_at)
      }));
      
      setTransactions(transactionsWithDates);
      console.log(`âœ… Refreshed ${transactionsWithDates.length} transactions from Supabase`);
    } catch (error) {
      console.error('âŒ Error refreshing transactions:', error);
    }
  }, [currentUserId]);

  // Real-time subscription for transactions
  useEffect(() => {
    if (!currentUserId) return;

    const unsubscribe = subscribeToTransactions(currentUserId, (newTransaction) => {
      console.log('ðŸ”„ New transaction received via real-time subscription:', newTransaction);
      // Convert and add the new transaction to local state
      const transaction = {
        id: newTransaction.id!,
        type: newTransaction.type,
        amount: parseFloat(newTransaction.amount.toString()),
        category: newTransaction.category || '',
        description: newTransaction.description || '',
        date: new Date(newTransaction.created_at!)
      };
      
      setTransactions(prev => [transaction, ...prev]);
    });

    return () => {
      unsubscribe();
    };
  }, [currentUserId]);

  // Apply theme to document
  useEffect(() => {
    document.documentElement.setAttribute('data-theme', theme);
    localStorage.setItem('anita-theme', theme);
  }, [theme]);

  const handleTransactionAdd = (transaction: Transaction) => {
    const newTransactions = [...transactions, transaction];
    setTransactions(newTransactions);
    // Save to localStorage as backup
    localStorage.setItem('anita-transactions', JSON.stringify(newTransactions));
    console.log('âœ… Transaction added to local state:', transaction);
  };

  const handleMessageAdd = async (message: Message) => {
    const newMessages = [...messages, message];
    setMessages(newMessages);
    
    // Save to Supabase
    if (currentUserId) {
      try {
        console.log('ðŸ”„ Saving message to Supabase:', message.text);
        await saveMessage({
          user_id: currentUserId,
          content: message.text,
          message_type: message.sender === 'anita' ? 'bot' : 'user'
        }, currentUserId);
        
        console.log('âœ… Message saved successfully to Supabase');
        // Also save to localStorage as backup
        localStorage.setItem('anita-messages', JSON.stringify(newMessages));
      } catch (error) {
        console.error('âŒ Error saving message to Supabase:', error);
        // Still save to localStorage as backup
        localStorage.setItem('anita-messages', JSON.stringify(newMessages));
      }
    } else {
      // No user ID, just save to localStorage
      localStorage.setItem('anita-messages', JSON.stringify(newMessages));
    }
    
    // Record message using the message recorder
    if (window.anitaMessageRecorder) {
      window.anitaMessageRecorder.recordMessage(
        message.text, 
        message.sender === 'anita' ? 'bot' : 'user',
        {
          transaction: message.transaction,
          originalId: message.id
        }
      );
    }
  };

  const handleThemeChange = (newTheme: Theme) => {
    setTheme(newTheme);
  };

  // Calculate financial summary
  const totalIncome = transactions
    .filter(t => t.type === 'income')
    .reduce((sum, t) => sum + t.amount, 0);
  
  const totalExpenses = transactions
    .filter(t => t.type === 'expense')
    .reduce((sum, t) => sum + t.amount, 0);
  
  const netBalance = totalIncome - totalExpenses;
  
  // Get all transactions sorted by date (newest first)
  const allTransactions = [...transactions]
    .sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime());

  return (
    <div className="app">
      <Sidebar 
        onViewChange={setCurrentView} 
        currentView={currentView}
        totalBalance={netBalance}
        monthlyIncome={totalIncome}
        monthlyExpenses={totalExpenses}
        recentTransactions={allTransactions}
      />
      <div className="main-content">
        {currentView === 'chat' && (
          <ChatInterface 
            onTransactionAdd={handleTransactionAdd} 
            onMessageAdd={handleMessageAdd} 
            messages={messages}
            currentUserId={currentUserId}
          />
        )}
        {currentView === 'settings' && <Settings onThemeChange={handleThemeChange} currentTheme={theme} />}
        {currentView === 'finance' && <FinancePage currentUserId={currentUserId} />}
      </div>
    </div>
  );
};

export default App;
