import React, { useState } from 'react';
import { 
  MessageCircle, 
  Settings, 
  TrendingUp, 
  DollarSign,
  BarChart3,
  Users,
  User
} from 'lucide-react';
import './Sidebar.css';

interface Transaction {
  id: string;
  type: 'income' | 'expense';
  amount: number;
  category: string;
  description: string;
  date: Date;
  userId?: string;
  username?: string;
}

interface User {
  id: string;
  username: string;
  display_name: string;
  email?: string;
  created_at: string;
}

interface SidebarProps {
  onViewChange: (view: 'chat' | 'settings' | 'finance') => void;
  currentView: 'chat' | 'settings' | 'finance';
  totalBalance: number;
  monthlyIncome: number;
  monthlyExpenses: number;
  recentTransactions?: Transaction[];
}

const Sidebar: React.FC<SidebarProps> = ({ 
  onViewChange, 
  currentView, 
  totalBalance, 
  monthlyIncome, 
  monthlyExpenses, 
  recentTransactions = [],
  currentUser,
  users = [],
  onUserChange
}) => {
  const [showUserMenu, setShowUserMenu] = useState(false);

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <div className="logo">
          <div className="logo-icon">A</div>
          <span className="logo-text">ANITA</span>
        </div>
        <p className="tagline">Your Finance AI Assistant</p>
        
        {/* User selector */}
        <div className="user-selector">
          <div className="current-user" onClick={() => setShowUserMenu(!showUserMenu)}>
            <User size={16} />
            <span>{currentUser?.display_name || 'Select User'}</span>
          </div>
          {showUserMenu && (
            <div className="user-menu">
              <div className="user-menu-header">
                <Users size={16} />
                <span>Switch User</span>
              </div>
              {users.map((user) => (
                <div
                  key={user.id}
                  className={`user-option ${currentUser?.id === user.id ? 'active' : ''}`}
                  onClick={() => {
                    onUserChange?.(user.username);
                    setShowUserMenu(false);
                  }}
                >
                  <div className="user-info">
                    <span className="user-name">{user.display_name}</span>
                    <span className="user-username">@{user.username}</span>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      <div className="sidebar-nav">
        <button 
          className={`nav-item ${currentView === 'chat' ? 'active' : ''}`}
          onClick={() => onViewChange('chat')}
        >
          <MessageCircle size={20} />
          <span>Chat with ANITA</span>
        </button>
        <button 
          className={`nav-item ${currentView === 'finance' ? 'active' : ''}`}
          onClick={() => onViewChange('finance')}
        >
          <BarChart3 size={20} />
          <span>Finance Dashboard</span>
        </button>
        <button 
          className={`nav-item ${currentView === 'settings' ? 'active' : ''}`}
          onClick={() => onViewChange('settings')}
        >
          <Settings size={20} />
          <span>Settings</span>
        </button>
      </div>

      <div className="sidebar-footer">
        <div className="quick-stats">
          <div className="quick-stat">
            <DollarSign size={16} />
            <span>Total Balance</span>
            <span className={`stat-value ${totalBalance >= 0 ? 'positive' : ''}`}>
              ${totalBalance.toLocaleString()}
            </span>
          </div>
          <div className="quick-stat">
            <TrendingUp size={16} />
            <span>This Month Income</span>
            <span className="stat-value positive">+${monthlyIncome.toLocaleString()}</span>
          </div>
          <div className="quick-stat">
            <DollarSign size={16} />
            <span>This Month Expenses</span>
            <span className="stat-value">-${monthlyExpenses.toLocaleString()}</span>
          </div>
        </div>
        
        <div className="recent-transactions">
          <h4>All Transactions ({recentTransactions.length})</h4>
          {recentTransactions.length > 0 ? (
            <div className="recent-transactions-list">
              {recentTransactions.map((transaction) => (
                <div key={transaction.id} className={`recent-transaction ${transaction.type}`}>
                  <div className="transaction-info">
                    <span className="transaction-desc">{transaction.description}</span>
                    <span className="transaction-category">{transaction.category}</span>
                  </div>
                  <span className={`transaction-amount ${transaction.type}`}>
                    {transaction.type === 'income' ? '+' : '-'}${transaction.amount.toFixed(2)}
                  </span>
                </div>
              ))}
            </div>
          ) : (
            <div className="no-transactions-sidebar">
              <p>No transactions yet. Start chatting with ANITA!</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
